// @ts-nocheck
import { createResolver, defineNuxtModule, addServerHandler, addPlugin } from '@nuxt/kit';
export default defineNuxtModule({
    setup(options, nuxt) {
        const resolver = createResolver(import.meta.url);
        addPlugin({
            src: resolver.resolve('./firebase/init.js'),
            mode: 'client'
        });
        addServerHandler({
            route: '/api/flowmate/session',
            handler: resolver.resolve('./server/api/session.js'),
        });
        addServerHandler({
            route: '/api/flowmate/**',
            handler: resolver.resolve('./server/api/flowmate.js')
        });
    }
});
